import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState('');

  const register = async () => {
    try {
      await axios.post('http://localhost:5000/register', { username, password });
      alert('User registered');
    } catch (err) {
      alert('Error registering user');
    }
  };

  const login = async () => {
    try {
      const res = await axios.post('http://localhost:5000/login', { username, password });
      setToken(res.data);
      alert('Logged in');
    } catch (err) {
      alert('Error logging in');
    }
  };

  const getUser = async () => {
    try {
      const res = await axios.get('http://localhost:5000/user', { headers: { 'auth-token': token } });
      alert(`User: ${res.data.username}`);
    } catch (err) {
      alert('Error fetching user');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>User Authentication</h1>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        style={{ display: 'block', margin: '10px 0', padding: '5px' }}
      />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={{ display: 'block', margin: '10px 0', padding: '5px' }}
      />
      <button
        onClick={register}
        style={{ margin: '5px', padding: '10px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '5px' }}
      >
        Register
      </button>
      <button
        onClick={login}
        style={{ margin: '5px', padding: '10px', backgroundColor: '#008CBA', color: 'white', border: 'none', borderRadius: '5px' }}
      >
        Login
      </button>
      <button
        onClick={getUser}
        style={{ margin: '5px', padding: '10px', backgroundColor: '#f44336', color: 'white', border: 'none', borderRadius: '5px' }}
      >
        Get User
      </button>
    </div>
  );
};

export default App;
